local function CheckForFemal( client )
	local Char = client:GetCharacter()
	local Desc = Char:GetDescription()
	local FemaleStart, FemaleEnd = string.find( Desc:lower(), "female" )
	local CharFeml = false
	if FemaleStart then
		CharFeml = true
	else
		if client:IsFemale() then
			CharFeml = true
		else
			CharFeml = false
		end
	end
	if CharFeml then
		return CharFeml
	end
	
	return false
end

